flow.func:
//msg.action=msg.payload.value.action;

msg.url=msg.payload.value.url;
msg.creds=msg.payload.value.creds;

msg.oldpayload=msg.payload;
msg.payload={};
msg.payload=msg.oldpayload.value.values;

msg.headers={};
var auth = 'Basic ' + new Buffer(msg.creds).toString('base64');
msg.headers = {
    "Authorization": auth
}
return msg;

msg.newpayload=msg.payload;
msg.payload=[];
msg.payload=msg.oldpayload;
msg.payload.value=msg.newpayload;

msg.payload.value.myvar=env.get('setsplit');
return msg;

