//Utility obj is { protocol:val, endpoint:val} OR endpoint_string
// -> kb_string (with prefix "i:" or <> )
//if protocol includes _ it also has a method.
const kb_protocol_builder = (obj) => {
    if(typeof obj != "object"){
        console.log("kb_protocol_builder: Invalid input"+ obj);
        return;
    }
    if(typeof obj.protocol != "string" || typeof obj.endpoint != "string"){
        console.log("kb_protocol_builder: Input object must be { protocol:val, endpoint:val}");
        return;
    }

    const protocol_types = [
        "HTTP_GET",
        "HTTP_POST",
        "AMQP",
        "MQTT",
        "WebSocket"
    ];
    //this maps to the above array!!!
    const protocol_prefs = [
        ["http://","https://"],
        ["http://","https://"],
        ["amqp://","amqps://"],
        ["mqtt://","mqtts://"],
        ["ws://"  ,"wss://"  ]
    ];

    let protocol = obj.protocol.replace(" ","_");
    let endpoint = obj.endpoint;
    ret = "";
    for(let i = 0; i< protocol_types.length; i++){
        if(protocol==protocol_types[i]){//Supported protocol
            //If the designated protocol prefixes are not included
            if(!( endpoint.includes(protocol_prefs[i][0]) || endpoint.includes(protocol_prefs[i][1]) )){
                if(endpoint.includes("://")){//Wrong protocol.
                    console.log("kb_protocol_builder: Input endpoint has wrong protocol prefix!");
                    return;
                }//otherwise add default.
                endpoint = protocol_prefs[i][0] + endpoint;
            }
            //Build KB string.
            if(protocol.includes("_")){
                ret = `"${protocol}:${endpoint}"`;
                break;
            }else{
                ret = `<${endpoint}>`;
                break;
            }
        }
    }
    if(ret === ""){
        console.log("Incorrect protocol type input!");
        return;
    }
    return ret;
};
global.set("kb_protocol_builder",kb_protocol_builder);

//Utility kb_string -> { protocol:val, endpoint:val }
const kb_protocol_filter = (endpoint) => {
    if(typeof endpoint != "string"){
        console.log("kb_protocol_filter: Invalid input"+ endpoint);
        return;
    }

    const protocol_types = [
        "HTTP_GET",
        "HTTP_POST",
        "AMQP",
        "MQTT",
        "WebSocket"
    ];
    //this maps to the above array!!! //Allways included in kb!
    const protocol_prefs = [
        ["http://","https://"],
        ["http://","https://"],
        ["amqp://","amqps://"],
        ["mqtt://","mqtts://"],
        ["ws://"  ,"wss://"  ]
    ];

    let ret = {};
    for(let i = 0; i< protocol_types.length; i++){
        //If protocol is found
        if(endpoint.includes(protocol_prefs[i][0]) || endpoint.includes(protocol_prefs[i][1])){
            if(protocol_types[i].includes("_")){//If it has a method included.
                temp = endpoint.replace(':','...' );
                obj = temp.split('...');
                ret.protocol = protocol_types[i];
                if (obj[0] != protocol_types[i]) continue;//skip if both protocol and method are not the same.
                ret.endpoint = obj[1];
                break;
            }
            ret.endpoint = endpoint;
            ret.protocol = protocol_types[i];
            break;
        }
    }

    if(ret.endpoint === undefined){
        console.log("kb_protocol_filter: Wraning! Unforeseen Input: "+ endpoint);
        ret.endpoint = endpoint;
        for (let i=0; i<protocol_types.length; i++){
            if(ret.endpoint.includes(protocol_types[i]+":")){
                ret.endpoint = ret.endpoint.replace(':','...' );
                obj = endpoint.split('...');
                ret.protocol = obj[0];
                ret.endpoint = obj[1];
                break;
            }
        }
    }
    ret.protocol = ret.protocol.replace("_"," ");
    return ret;
};
global.set("kb_protocol_filter", kb_protocol_filter);

