// Check if node type is supported
const supportedNodeType = (nodeType) => {
    if (typeof nodeType != "string") {
        console.error(`supportedNodeType: nodeType input ${nodeType} is not a string!`);
        return false;
    }

    if (!!flow.get("supportedNodes")[nodeType]) {
        return true;
    }

    if (nodeType.includes(":")) {
        const localTypes = nodeType.split(":");
        for (const localType of localTypes) {
            if (supportedNodeType(localType)) {
                return true;
            }
        }
        return false;
    }

    if (nodeType.includes(" ")) {
        const localTypes = nodeType.split(" ");
        for (const localType of localTypes) {
            if (supportedNodeType(localType)) {
                return true;
            }
        }
        return false;
    }

    return false;
};


