// Add object-property-value to a temp triples index for multiple objects.
const tempTripleToIndex = (object, property, value, tempTriplesIndex) => {
    // Not a pure function! An empty or non empty object is changed here!
    // tempTriplesIndex: { object: { property: [value] } }
    // For multiple values, input all values in an array as value parameter.
    if (!(object in tempTriplesIndex)) {
        tempTriplesIndex[object] = {};
    }
    if (!(property in tempTriplesIndex[object])) {
        tempTriplesIndex[object][property] = [];
    }
    if (Array.isArray(value)) {
        const temp = tempTriplesIndex[object][property];
        tempTriplesIndex[object][property] = temp.concat(value);
    } else tempTriplesIndex[object][property].push(value);
};

// Clean a temp triples index. Meant for the same type of index as above!
const cleanTriplesIndex = (tempTriplesIndex) => {
    // Not a pure function! Input object is changed here!
    // tempTriplesIndex: { object: { property: [value] } }
    for (const object in tempTriplesIndex) {
        for (let property in tempTriplesIndex[object]) {
            // Remove any duplicate values.
            if (tempTriplesIndex[object][property].length > 1) {
                let temp = tempTriplesIndex[object][property];
                tempTriplesIndex[object][property] = [...new Set(temp)];
            }

            // If only one value, set value directly.
            if (tempTriplesIndex[object][property].length == 1) {
                tempTriplesIndex[object][property] = tempTriplesIndex[object][property][0];
            }
        }
    }
};

// Add a property value as in semantics, in a JS object for semantics.
// Does NOT take array value.
const addPropertyValue = (js_obj, property, value) => {
    if (!(property in js_obj)) {
        js_obj[property] = value;
    } else {
        // Results in array.
        if (!(Array.isArray(js_obj[property]))) {
            const temp = js_obj[property];
            js_obj[property] = [temp, value];
        } else js_obj[property].push(value);
        // Removes duplicates
        js_obj[property] = [...new Set(js_obj[property])]
    }
};

// Deep clean all empty and faulty values, even NaN.
const cleanObj = (obj) => {
    // @ts-ignore
    return cleanDeep(obj, NaNValues = true);
};

// First letter uppercase, rest lowercase
const firstLetter = (str) => {
    return str.replace(/^.{1}/g, str[0].toUpperCase());
};

// Canonical Semantic property or class name style
const toSemanticName = (str, isProperty = false) => {
    if (isAbsolutePath(str) || isUri(str)) {
        return str;
    }

    let newStr = "";
    let i = 0;
    for (const part of str.split(" ")) {
        if (isProperty && i == 0) {
            for (let subPart of part.split(/(?=[A-Z])/)) {
                if (isProperty && i == 0) {
                    newStr = newStr.concat(subPart.toLowerCase());

                } else newStr = newStr.concat(subPart);
                i++;
            }
        } else newStr = newStr.concat(firstLetter(part));
        i++;
    }
    return newStr;
};

const bestStringMatch = (str, strArray) => {
    return stringSimilarity.findBestMatch(str, strArray).
        bestMatch.target;
};

const isObject = (item) => {
    return (item && typeof item === 'object' && !Array.isArray(item));
}

const mergeDeep = (target, ...sources) => {
    if (!sources.length) return target;
    const source = sources.shift();

    if (isObject(target) && isObject(source)) {
        for (const key in source) {
            if (isObject(source[key])) {
                if (!target[key]) Object.assign(target, { [key]: {} });
                mergeDeep(target[key], source[key]);
            } else {
                Object.assign(target, { [key]: source[key] });
            }
        }
    }

    return mergeDeep(target, ...sources);
}

const isEmptyObject = (obj) => {
    return Object.keys(obj).length === 0;
