// Find the appropriate reference for the dependency
const libraryDependencyTemplate = (functionNode, lib) => {
    if (validNpmPackage(lib.module)) {
        return `js_lib:${lib.module}`;
    }
    if (isRelativePath(lib.module) || isAbsolutePath(lib.module)) {
        // Means local dependency.
        return `function:${functionNode.id}/dependency/${lib.var}#path=${lib.module}`
    }
    return ""
}

// Create the library imports for function nodes.
const transformLibs = (functionNode) => {

    let libImports = [];

    if (!("libs" in functionNode) ||
        functionNode.libs === undefined ||
        functionNode.libs === null)
        return libImports;

    for (const lib of functionNode.libs) {
        libImports.push({
            "@id": `function:${functionNode.id}/dependency/${lib.var}`,
            "@type": "SoftwareLibrary",
            "label": lib.var,
            // js_lib is the scheme to reference the library.
            // The uri should link to the npm (or yarn) package.
            "hasTemplate": libraryDependencyTemplate(functionNode, lib),
            "codeSymbol": lib.var,
        });
    }

    return libImports;
}

