msg.headers ['x-api-key'] = flow.get('RFkey');

if (msg.payload.length>1){
    msg.payload=msg.payload[0];
}
return msg;

const {
    isUri,
    addPropertyValue,
} = flow.get("utilFunctions");

const originalAppPayload = msg.payload;
const displayName = originalAppPayload.displayName;

const oldUserId = originalAppPayload.userId;
let newUserId = '';
if (oldUserId) {
    newUserId = isUri(oldUserId) ? oldUserId : `owner:${oldUserId}`;
}

msg.payload = {
    '@id': (oldUserId) ?
        `${newUserId}/application/${displayName}` :
        `app:${displayName}`,
    '@type': 'Application',
    'isTopLevelComposite': true, // Override the inference.
}; // Application Semantic representation.

if (oldUserId) addPropertyValue(
    msg.payload,
    'hasOwner',
    newUserId,
);

return msg;

// Message for the specific flow
let flowMsg = msg;
flowMsg.payload.nodes= [];
const flowNodes = flowMsg.payload.flow;
delete flowMsg.payload.flow;

// Message containing all the required subflows.
let subflowsMsg = RED.util.cloneMessage(msg);
subflowsMsg.payload = {};

// Error message to be propagated. It is not null when error occurs.
let errorMsg = RED.util.cloneMessage(msg);
errorMsg.payload = null;

let requiredSubflowIDs = [];

for (let flowNode of flowNodes) {
    console.log('for');
    if (flowNode.type == "subflow") {
        subflowsMsg.payload[flowNode.id] = flowNode;
        // Will be further pre-processed later.
    } else {
        console.log('to push');
        flowMsg.payload.nodes.push(flowNode);
        console.log(flowNode);
        if (flowNode.type.includes("subflow:")) {
            requiredSubflowIDs.push(flowNode.type.split(':')[1]);
        }
        console.log('checked');
    }
}

// Check that all subflow definitions exist.
for (let requiredID of requiredSubflowIDs) {
    if ( !(requiredID in subflowsMsg.payload) ) {
        errorMsg.payload = "Missing one or more required subflow definitions. Please add missing subflow definitions in 'flow' array.";
        return [null, null, errorMsg];
    }
}

// Set only acceptable fields
flowMsg.payload.id = flowMsg.payload.flowID;
delete flowMsg.payload.flowID;
flowMsg.payload.label = flowMsg.payload.flowName;
delete flowMsg.payload.flowName;
// Only artifact and artifactType acceptable
flowMsg.payload.artifactType = flowMsg.payload.type;
delete flowMsg.payload.type;

return [subflowsMsg, flowMsg, errorMsg];

if (msg.payload == null) return;

const results = msg.payload;

msg.subflows = {};
msg.annotators = {};

for (const subflowId in results){
    const subflow = results[subflowId];
    msg.subflows[subflow.id] = subflow;
    if(subflow.category == "PHYSICS Annotators"){
        msg.annotators[subflow.id] = subflow;
    }
}

msg.payload = {};

return msg;

return {
    'payload': msg.payload,
    'topic': msg.topic,
    // Manual override of parts, in order to properly join with app
    'parts': {
        "id": msg.topic.replace('extract_', ''),
        "count":2,
        "len":1,
        "index":1,
        "type":"array"
    }
};

msg.branchName=msg.payload.branchName;

if (! msg.payload || !msg.payload.flows){
    msg.data="Invalid input";
    msg.payload="Empty input";
    return [msg,null];
}

if (msg.payload.flows.length==0){
    msg.data='Flows can not be empty';
    msg.payload='Flows can not be empty';
    return [msg,null];
} else {
    return [null,msg];
}

