async function getFunctionProfile(functionId = null, location = null) {
    return new Promise((resolve, reject) => {
        const path = (functionId) ? ((location) ? `/profile/${functionId}/${location}` : `/profile/${functionId}/`) : "/profile/";
        const url = new URL(path, pefProfilesUrl).toString();

        const req = https.request(url, options, (res) => {
            // Handle the response
            let data = '';

            res.on('data', (chunk) => {
                data += chunk;
            });

            res.on('end', () => {
                if (res.statusCode >= 200 && res.statusCode < 300) {
                    // Successful Request
                    resolve(JSON.parse(data));
                } else {
                    reject(new Error(`Request failed with HTTP status code ${res.statusCode}`));
                }
            });
        });

        req.on('error', (error) => {
            reject(error);
        });

        req.end();
    });
}

