/**
* Parsing required for loadGenData returned by the 
*  PEF API call in the next function.
* @param {string} loadGenDataStr
*/
async function parseLoadGenData(loadGenDataStr) {

    let loadGenDataObj = {}; // RETURN VALUE
    loadGenDataObj.rows = []; // A view of all the rows
    loadGenDataObj.targetData = {}

    let targetLoadGenData = []; // Target or temporary target data

    const loadGenDataArray = JSON.parse(loadGenDataStr);

    for (let loadGenDataRow of loadGenDataArray) {
        let loadGenDataOutput = {};

        if (typeof loadGenDataRow.output === "string") {
            loadGenDataOutput = JSON.parse(loadGenDataRow.output);
        } else {
            loadGenDataOutput = loadGenDataRow.output;
        }

        if (!loadGenDataOutput) continue;

        loadGenDataOutput.activationid = loadGenDataRow.activationid;
        loadGenDataOutput.flow = loadGenDataRow.flow;

        loadGenDataObj.rows.push(loadGenDataOutput);

        // Find the target data
        if (loadGenDataOutput.status &&
            loadGenDataOutput.status === "Completed" &&
            loadGenDataOutput.inputData &&
            loadGenDataOutput.inputData.rawData
        ) {

            // THIS FILTERING IS TO BE KEPT INTACT!
            let filteredLoadGenData = {};

            filteredLoadGenData.location = loadGenDataRow.location;
            filteredLoadGenData.achievedAverageRate = loadGenDataOutput.achievedAverageRate;
            filteredLoadGenData.averageDuration = loadGenDataOutput.averageDuration;
            filteredLoadGenData.averageInitTime = loadGenDataOutput.averageInitTime;
            filteredLoadGenData.averageStartLatency = loadGenDataOutput.averageStartLatency;
            filteredLoadGenData.averageUserSideDelay = loadGenDataOutput.averageUserSideDelay;
            filteredLoadGenData.averageWaitTime = loadGenDataOutput.averageWaitTime;
            filteredLoadGenData.launchGeneratorDelay = loadGenDataOutput.launchGeneratorDelay;
            filteredLoadGenData.memory = loadGenDataOutput.memory;
            filteredLoadGenData.sampleNumber = loadGenDataOutput.sampleNumber;
            filteredLoadGenData.setRate = loadGenDataOutput.setRate;
            filteredLoadGenData.stdDevDuration = loadGenDataOutput.stdDevDuration;
            filteredLoadGenData.stdDevInitTime = loadGenDataOutput.stdDevInitTime;
            filteredLoadGenData.stdDevStartLatency = loadGenDataOutput.stdDevStartLatency;
            filteredLoadGenData.stdDevUserSideDelay = loadGenDataOutput.stdDevUserSideDelay;
            filteredLoadGenData.stdDevWaitTime = loadGenDataOutput.stdDevWaitTime;
            filteredLoadGenData.successPercentage = loadGenDataOutput.successPercentage;
            filteredLoadGenData.globalStartTime = loadGenDataOutput.globalStartTime;
            // ADD FIELDS IF REQUIRED


            targetLoadGenData.push(filteredLoadGenData);



        }

    }
    // The filtered values to be used
    loadGenDataObj.targetData = JSON.parse(JSON.stringify(targetLoadGenData));
    // Essentially the above is the one with the max globalStartTime
    // Which should have been 

    if (loadGenDataObj.targetData.globalStartTime) {
        loadGenDataObj.targetData.loadGenStartTimestamp = loadGenDataObj.targetData.globalStartTime;
        delete loadGenDataObj.targetData.globalStartTime;
    }

    return loadGenDataObj;
}

async function getLoadGenData(flowName = null, branchName = null, unfiltered = false) {
    return new Promise((resolve, reject) => {
        if (!flowName || !branchName) {
            reject(new Error(`getLoadGenData: Invalid parameters! flowName: ${flowName} branchName: ${branchName}`));
        }

        const path = `/loadgendata/${flowName}/${branchName}`;
        const url = new URL(path, pefProfilesUrl).toString();

        const req = https.request(url, options, (res) => {
            // Handle the response
            let data = '';

            res.on('data', (chunk) => {
                data += chunk;
            });

            res.on('end', () => {
                if (res.statusCode >= 200 && res.statusCode < 300) {

                    // Successful Request
                    if (unfiltered) {
                        resolve(JSON.parse(data));
                    } else {
                        resolve(parseLoadGenData(data));
                    }
                } else {
                    reject(new Error(`Request failed with HTTP status code ${res.statusCode}`));
                }
            });
        });

        req.on('error', (error) => {
            reject(error);
        });

        req.end();
    });
}

flow.set("getLoadGenData", getLoadGenData);


