console.log("");
console.log("Semantic Transformation - Phase 1 ==============================");
// Generic function import -----------------------------------------------------
const { 
    validNpmPackage,
    isRelativePath,
    isAbsolutePath,
    tempTripleToIndex,
    cleanTriplesIndex,
    addPropertyValue,
    toSemanticName,
} = flow.get("utilFunctions");
const codeAnnotationExtractor = flow.get("codeAnnotationExtractor");

// Transformation helper functions ---------------------------------------------
// Find the appropriate reference for the dependency
const libraryDependencyTemplate = (functionNode, lib) => {
    if (validNpmPackage(lib.module)) {
       return `js_lib:${lib.module}`;
    }
    if (isRelativePath(lib.module) || isAbsolutePath(lib.module)) {
        // Means local dependency.
        return `function:${functionNode.id}/dependency/${lib.var}#path=${lib.module}`
    }
    return ""
}

// Create the library imports for function nodes.
const transformLibs = (functionNode) => {

    let libImports = [];

    if ( !("libs" in functionNode) ||
        functionNode.libs === undefined ||
        functionNode.libs === null)
        return libImports;

    for (const lib of functionNode.libs){
        libImports.push({
            "@id": `function:${functionNode.id}/dependency/${lib.var}`,
            "@type": "SoftwareLibrary",
            "label": lib.var,
            // js_lib is the scheme to reference the library.
            // The uri should link to the npm (or yarn) package.
            "hasTemplate": libraryDependencyTemplate(functionNode, lib),
            "codeSymbol": lib.var,
        });
    }

    return libImports;
}

// Filters ---------------------------------------------------------------------
const subflowFilter = jsonata("[nodes.*[$contains(type, 'subflow:')]]");
const functionsFilter = jsonata("[nodes.*[type='function']]");
const httpNodesFilter = jsonata("[nodes.*[$contains(type, 'http')]]");

// Node collections (Lists) ----------------------------------------------------
console.log("Node collections ===============================================");
const subflowNodes = subflowFilter.evaluate(msg.payload);
let functionNodes = functionsFilter.evaluate(msg.payload);
let httpNodes = httpNodesFilter.evaluate(msg.payload);
let nodes = []; // Other nodes. To be filled with other types later.

// Monitor
console.log("--------------------------");
console.log("subflowNodes", util.inspect(subflowNodes, false, null, true));
console.log("--------------------------");
console.log("functionNodes", util.inspect(functionNodes, false, null, true));
console.log("--------------------------");
console.log("httpNodes", util.inspect(httpNodes, false, null, true));
console.log("--------------------------");
console.log("Other nodes", util.inspect(nodes, false, null, true));
console.log("--------------------------");

// New constructs from semantics -----------------------------------------------
console.log("New constructs for semantics ===================================");
// Indexes ( simple id : boolean )
let appInterfacesIndex = {}; // Application Interfaces (all types) (OLD ID)
let nonWireAppInterfaces = {}; // TO EXCLUDE FROM WIRE TRANSFORMATION (OLD ID)
// Temporary triples from parsing
let flowTriples = {}; // Flow id (complete): {property: [ value]} 
let functionTriples = {}; // Function id (complete): {property:[ value]}
// Other triples from any parsing operation (index).
let otherTriples = {}; // id (complete or `unset:${var}`) : {property:[ value] }
// ID indexes, to be used in any situation
let oldToNewID = {}; // Old id : new id
let newToOldID = {}; // New id : old id
//Set ID indexes for flow and functions.
oldToNewID[msg.payload.id] = "flow:"+msg.payload.id;
newToOldID["flow:"+msg.payload.id] = msg.payload.id;
for (const oldFunctionNode of functionNodes) {
    oldToNewID[oldFunctionNode.id] = "function:"+oldFunctionNode.id;
    newToOldID["function:"+oldFunctionNode.id] = oldFunctionNode.id;
}

/*
NOTE: Triple indexes may have the "unset:" prefix in the key. This means that 
the id has uncertain matching, i.e. new ID of new entity is not known. It is 
fixed up using the fixUnset function.
*/
// Fix all unset IDs
const fixUnset = (triplesIndex) => {
    for ( const object in triplesIndex ){
        if(! object.includes("unset:")) continue;

        const oldId = object.replace("unset:", "");
        if ( oldId in oldToNewID ) {
            // Match found. Replace ID.
            triplesIndex[oldToNewID[oldId]] = triplesIndex[oldId];
            delete triplesIndex[oldId];

        }else {
            // Match not found. Add to otherTriples.
            for ( const property in triplesIndex[object] ){
                tempTripleToIndex(
                    object,
                    property,
                    triplesIndex[object][property],
                    otherTriples,
                );
            }
        }
    }
};

// Monitor
console.log("--------------------------");
console.log("oldToNewID", util.inspect(oldToNewID, false, null, true));
console.log("--------------------------");
console.log("newToOldID", util.inspect(newToOldID, false, null, true));
console.log("--------------------------");

// Transformation of NON-workflow nodes ----------------------------------------
// Transformation for httpNodes ------------------------------------------------
console.log("HTTP Nodes Transformation ======================================");
for (let httpNode of httpNodes){
    const newId = `flow:${httpNode.z}/interface/${httpNode.id}`
    oldToNewID[httpNode.id] = newId;
    newToOldID[newId] = httpNode.id;

    // The new individual
    let httpAppInterface = {
        "@id": newId,
        "@type": "REST",
        "label": httpNode.name,
    };

    if (httpNode.type == "http response") {
        // Simple output.
        httpAppInterface["@type"] = "RESTOutput";
    } else {
        httpAppInterface["hasHttpMethod"] = httpNode.method.toUpperCase();
        httpAppInterface["interfaceURI"] = httpNode.url;

        if (httpNode.type == "http in"){
            // HTTP in node
            httpAppInterface["allowsUpload"] = httpNode.upload;
            httpAppInterface["swaggerDoc"] = httpNode.swaggerDoc;

            // add temporary Triple to index
            // Flow exposesREST this http interface
            tempTripleToIndex(
                `flow:${httpNode.z}`,
                "exposesREST",
                newId,
                flowTriples
            );

        } else if (httpNode.type == "http request") {
            // add temporary Triple to index
            // Flow consumesREST this http interface
             tempTripleToIndex(
                `flow:${httpNode.z}`,
                "consumesREST",
                newId,
                flowTriples
            );
        }
    }

    // Indexing required to avoid wrong wire parsing
    appInterfacesIndex[httpNode.id] = httpAppInterface;
    nonWireAppInterfaces[httpNode.id] = httpAppInterface;
}

// Monitor
console.log("--------------------------");
console.log("appInterfacesIndex", util.inspect(appInterfacesIndex, false, null, true));
console.log("--------------------------");
console.log("nonWireAppInterfaces", util.inspect(nonWireAppInterfaces, false, null, true));
console.log("--------------------------");

// -----------------------------------------------------------------------------

// Transformation of workflow nodes --------------------------------------------
console.log("Workflow Nodes Transformation ==================================");
let completeWorkflowNodes = {}; // COMPLETED nodes for THIS PHASE (OLD ID)
// Transformation for functionNodes --------------------------------------------
cleanTriplesIndex(functionTriples); // Cleaned triples for functions.
fixUnset(functionTriples); // Clean up any unset triple
let triplesFromFunctions = [];
let semanticFunctionNodes = [];
for (const oldFunctionNode of functionNodes) {
    // Extract function code annotations and semantics. FUNCTION-LEVEL EXTRACTOR
    const { 
        functionCode,
        functionAnnotations,
        triples,
    } = codeAnnotationExtractor(oldFunctionNode.func);

    const newFuncID = "function:"+oldFunctionNode.id

    // The new function individual
    let newFunctionNode = {
        "@id": newFuncID,
        "@type": "Function",
        "label": oldFunctionNode.name,
        "initCode": oldFunctionNode.initialize,
        "funcCode": functionCode,
        "finalCode": oldFunctionNode.finalize,
        "callsDependency": transformLibs(oldFunctionNode),
    };

    for (const annotation of functionAnnotations) {
        addPropertyValue(
            newFunctionNode,
            toSemanticName(annotation.property, true),
            toSemanticName(annotation.value),
        );
    }

    for (let triple of triples) {
        // Fix their naming.
        triple = {
            "object": toSemanticName(triple.object ),
            "property": toSemanticName(triple.property ),
            "value": toSemanticName(triple.value ),
        }
    }

    // External triples that come from inserted functions. To be processed later.
    triplesFromFunctions = triplesFromFunctions.concat(triples);

    // Get all temporary triples for the function and add them.
    if (newFuncID in functionTriples){
        for ( const property in  functionTriples[newFuncID] ){
            newFunctionNode[property] = functionTriples[newFuncID][property]
        }
        delete functionTriples[newFuncID]; // Not to go to otherTriples.
    }
    // Function complete
    semanticFunctionNodes.push(newFunctionNode);
    completeWorkflowNodes[oldFunctionNode.id] = newFunctionNode;
}

// Monitor
console.log("--------------------------");
console.log("semanticFunctionNodes", util.inspect(semanticFunctionNodes, false, null, true));
console.log("--------------------------");
console.log("completeWorkflowNodes", util.inspect(completeWorkflowNodes, false, null, true));
console.log("--------------------------");

// -----------------------------------------------------------------------------

// Unparsed supported nodes NOW get an ID, in the first phase ------------------
console.log("Preparation of unparsed nodes ==================================");
const otherSupportedNodes =  {};
const oldFlowId = msg.payload.id;

// Pre-parsing of the rest...
for (const oldNode of []
    //NOTE: ADD ALL SUPPORTED NODES that are NOT MAIN! ( .concat( collection ) )
    .concat( subflowNodes )
){
    const oldNodeId= oldNode.id;

    const newOtherNodeId = `flow:${oldFlowId}/node/${oldNodeId}`;
    oldToNewID[oldNodeId] = newOtherNodeId;
    newToOldID[newOtherNodeId] = oldNodeId;
    completeWorkflowNodes[oldNodeId] = {
        "@id": newOtherNodeId,
    }
}

// Monitor
console.log("--------------------------");
console.log("completeWorkflowNodes", util.inspect(completeWorkflowNodes, false, null, true));
console.log("--------------------------");

// Transformation of all application interfaces --------------------------------
console.log("Application Interfaces Transformation ==========================");
const supportedNodes =  []
    //NOTE: ADD ALL SUPPORTED NODES!!! (.concat( node group ))
    .concat(subflowNodes)
    .concat(functionNodes)
    .concat(httpNodes);

console.log("supportedNodes", util.inspect(supportedNodes, false, null, true));

msg.appInterfaces = {};
// Parse all wires.
for (const oldNode of supportedNodes ){
    const oldNodeId = oldNode.id;
    // Avoid unsupported nodes.
    if(! (oldNodeId in oldToNewID) ) break;

    let isNonWire = false;
    const newNodeId = oldToNewID[oldNodeId];
    let appInterfaceId = "";
    let interfaceIndividual = {};

    if(oldNodeId in nonWireAppInterfaces){
        isNonWire = true;
        appInterfaceId = newNodeId;
        interfaceIndividual = nonWireAppInterfaces[oldNodeId];
    }

    let i = 0;
    for ( const wireOutput of oldNode.wires ) {

        // If new Message Wire should be created.
        if (! isNonWire) {
            // Create a new message wire app interface.
            appInterfaceId = `flow:${oldFlowId}/interface/${oldNodeId}.out${i}`;
            interfaceIndividual = {
                "@id": appInterfaceId,
                "@type": "MessageWire",
                "isNodeOutputOf": newNodeId,  // Current node
            }
        }

        let emptyMsgWire = true;
        // Iterate over destination connections.
        for(const oldDestID of wireOutput){

            // Check if this nr wire connection goes to non-wire app interface.
            if (oldDestID in nonWireAppInterfaces) {
                if (! isNonWire){
                    // This is a Workflow Node that has interface node as output.
                    addPropertyValue(
                        completeWorkflowNodes[oldNodeId], // Current node obj
                        "hasNodeOutput",
                        oldToNewID[oldDestID] // Destination node 
                    );
                } else{
                    // This is an interface node, and connects to interface node.
                    addPropertyValue(
                        interfaceIndividual,  // Current node obj
                        "interfaceToInterface",
                        oldToNewID[oldDestID]  // Destination node
                    );
                }
            } else { // Msg wire connection to workflow node.
                if (! isNonWire){
                    emptyMsgWire = false;
                    // This is a Workflow Node that connects to another, through
                    // the newly created message wire interface.
                    addPropertyValue(
                        interfaceIndividual,  // New Interface obj
                        "isNodeInputOf",
                        oldToNewID[oldDestID]  // Destination node
                    );
                } else {
                    // This is an interface Node that connects to Workflow node.
                    addPropertyValue(
                        interfaceIndividual, // Current node obj
                        "isNodeInputOf",
                        oldToNewID[oldDestID]    // Destination node
                    );
                }
            }
        }

        // Set interface individual appropriately. (Normal wire)
        if ( (!isNonWire) && (!emptyMsgWire) ){
            msg.appInterfaces[appInterfaceId] = interfaceIndividual;
        }

        i++; // Next wire...
    }

    // Set interface individual appropriately. (Not wire)
    if (isNonWire) msg.appInterfaces[appInterfaceId] = interfaceIndividual;

}

// Monitor
console.log("--------------------------");
console.log("msg.appInterfaces", util.inspect(msg.appInterfaces, false, null, true));
console.log("--------------------------");

// subflow Nodes check ---------------------------------------------------------
console.log("PHASE 1 ready ==================================================");

msg.tempSubflowData = subflowNodes;

//Phase 1 complete
// ID mapping
msg.oldToNewID = oldToNewID;
msg.newToOldID = newToOldID;
// Other triples extracted from functions.
msg.triplesFromFunctions = triplesFromFunctions;
// Semi-ready Flow
msg.payload = {  // Flow with functions.
    // Semantically ready
    "@id": "flow:"+oldFlowId,
    "@type": "Flow",
    "label": msg.payload.label,
    "hasFunction": semanticFunctionNodes,

    // To be further worked on
    "hasNode": nodes
}



fixUnset(flowTriples);
for (const flowId in flowTriples) {
    if(flowId == "flow:"+oldFlowId){
        msg.payload = {
            ...msg.payload,
            ...flowTriples[flowId],
        }
        delete flowTriples[flowId]; // Not to go to otherTriples.
    }
}

// Include other triples to be added in the end.
cleanTriplesIndex(otherTriples);
fixUnset(otherTriples);
msg.otherTriples = {
    ...otherTriples,
    ...flowTriples,
    ...functionTriples,
};

// Pass all the (old id : workflow Node)
msg.completeWorkflowNodes = completeWorkflowNodes;

// Monitoring end
console.log("Complete msg", util.inspect(msg, false, null, true));
console.log("END OF PHASE 1 =================================================");
console.log("");
console.log("");
console.log("");
console.log("");

return msg;

const { 
    tempTripleToIndex,
    cleanTriplesIndex,
    addPropertyValue,
    toSemanticName,
    bestStringMatch,
} = flow.get("utilFunctions");

console.log("Semantic Transformation - Phase 2 ==============================");

msg.payload = msg.payload[0];

let flowData = msg.payload; // The created Flow object with everything ready.
const oldSubFlows = msg.tempSubflowData;
console.log("---------------------------------------");
console.log("oldSubFlows", util.inspect(oldSubFlows, false, null, true));
console.log("---------------------------------------");

// Continue with subflows
const storedSubFlows = msg.subflows; // Old id : old subflow
const storedFlowAnnotators = msg.annotators; // Old id : old subflow

// console.log("storedSubFlows", util.inspect(storedSubFlows, false, null, true));
// console.log("---------------------------------------");
// console.log("storedFlowAnnotators", util.inspect(storedFlowAnnotators, false, null, true));
// console.log("---------------------------------------");

console.log("Subflow Preparation ============================================");
// Parse subflows
for (let oldSubflow of oldSubFlows) {
    oldSubflow["subflow"] = oldSubflow.type.replace('subflow:', '');

    // FLOW ANNOTATOR case.
    if (oldSubflow["subflow"] in storedFlowAnnotators){
        // Assumption: Annotator nodes are NOT CONNECTED to ANY NODE.
        // NOTE for later: Find a way to filter these through the ontology.
        for (const definitionObject of oldSubflow["env"] ){
            let value = "";
            if (definitionObject.type == "num") {
                value = Number(definitionObject.value);
            } value = definitionObject.value;

            addPropertyValue(
                msg.payload, // Flow object
                toSemanticName(definitionObject.name, true),
                value,
            ); 
        }

    }else {  // Add any subflow as node if not in annotators.
        let prepedNode = msg.completeWorkflowNodes[oldSubflow["id"]];

        // Note that its connections have already been prepared if they exist.
        addPropertyValue(
           msg.payload, // Flow object
           "hasNode",
           { // Subflow
               ...prepedNode, // Prepared id and input/output
               "@type": "SubFlow",
               "label": oldSubflow["name"],
               "hasTemplate": `flow:${oldSubflow["subflow"]}`
           }
        );
    }
}
// TODO LATER, if needed, add has Node for those not included.

// TODO Filter the annotations!

console.log("Interfaces Setup ===============================================");
// Sort out the defined Application Interfaces
const consumedRestApis = [];
const appInterfaces = [];
for (const interfaceId in msg.appInterfaces){
    const interfaceDefinition = msg.appInterfaces[interfaceId];
    // NOTE: ADD RULES TO THE INTERFACES THAT ARE NOT DEFINED.
    if (interfaceDefinition["@type"] == "REST" &&
        flowData.exposesREST &&
        !flowData.exposesREST.includes(interfaceId)
        ){  // From http request node
        consumedRestApis.push(interfaceDefinition);
    } else {
        appInterfaces.push(interfaceDefinition);
    }
}

console.log("---------------------------------------");
console.log("consumedRestApis", util.inspect(consumedRestApis, false, null, true));
console.log("---------------------------------------");
console.log("appInterfaces", util.inspect(appInterfaces, false, null, true));
console.log("---------------------------------------");


console.log("Software Artifact Setup ========================================");
const supportedArtifactTypes = [
    'SoftwareImage',
    'SoftwareBinary',
    'PackagedSourceCode'
];


let softwareArtifact = {
    '@id': msg.originalPayload.artifact,
};

if (msg.originalPayload.artifactType) {
    let artifactType = bestStringMatch(
        msg.originalPayload.artifactType,
        supportedArtifactTypes,
    );
    addPropertyValue(
        softwareArtifact,
        '@type',
        artifactType,
    );
}


console.log("END OF PHASE 2 =================================================");

msg.payload = { // Final JSON-LD payload for Flow:
    ...msg.payload,
    // Add interfaces
    "consumesREST": consumedRestApis,
    "definesInterface": appInterfaces,
    // Add original description
    "hasJSONDescription": msg.originalPayload,
    "hasSoftwareArtifact": softwareArtifact,
}

console.log("");
console.log("");
console.log("");
console.log("");
