// Parse line and return annotation parts
const parseLine = (line) => {
    // Skip comments entirely
    if (! line.includes("//")) return {
        "isAnnotationLine": false,
        "cleanedLine": line,
    };

    // Keep code before comment in the line
    let codePart = "";
    let commentLine = line;

    if (! line.startsWith("//")){
        let lineParts = line.split("//");
        [ lineParts[0], ...lineParts[1] ] = lineParts;
        lineParts[1] = lineParts[1].join('//');

        codePart=lineParts[0];
        commentLine='//'+lineParts[1]
    }

    let cleanComment = "";

    // Work on comment with the annotation starter
    if (commentLine.startsWith("//$") || commentLine.startsWith("//@")) {
        let annotation = {
            "object": "self", // self is reference to the function itself
            "property": "", // Annotation name/property
            "value": "", // Value, can be any type, depending on the property
        };

        let cleanLine = commentLine;

        // Keep rest of comment, if exists.
        if (commentLine.includes(" ")){
            let parts = [];
            [cleanLine, ...parts] = commentLine.split(" ");
            cleanComment = ' //'+parts.join(' ');
        }

        // Check if valid annotation
        if(cleanLine.startsWith("//$") && cleanLine.includes("@") && cleanLine.includes("=")) {
            // Triple
            let annotationLine = cleanLine.replace("//$", "");
            let parts = [];
            parts = annotationLine.split("@");
            [ parts[0], ...parts[1] ] = parts;
            parts[1] = parts[1].join('@');
            [ parts[1], ...parts[2] ] = parts[1].split("=");
            parts[2] = parts[2].join('=');

            annotation.object = parts [0];
            annotation.property = parts[1];
            annotation.value = parts[2];

        } else if(cleanLine.startsWith("//@") && cleanLine.includes("=")) {
            // Simple annotation
            let annotationLine = cleanLine.replace("//@", "");
            let parts = [];
            [parts[0], ...parts[1]] = annotationLine.split("=");
            parts[1] = parts[1].join('='); // = sign is supported in value

            annotation.property = parts[0];
            annotation.value = parts[1];


        // Not annotation
        } else return { 
            "isAnnotationLine": false,
            "cleanedLine":  line
        };

        // Empty fields are not supported!
        if(annotation.object === "" ||
           annotation.property === "" ||
           annotation.value === ""
        ) return {
            "isAnnotationLine": false,
            "cleanedLine": line,
        }

        // Annotation + Cleaned code + comment
        return {
            "isAnnotationLine": true,
            "annotation": annotation,
            "cleanedLine": codePart + cleanComment,
        }
    }

    // Not an annotation
    return { 
        "isAnnotationLine": false,
        "cleanedLine": line 
    };
}

// Main code annotation extractor
msg.codeAnnotationExtractor = (functionCode) => {
    let functionAnnotations = [];
    let triples = [];
    let cleanedCode = "";

    let lines = functionCode.split("\n");
    let cleanLines = [];

    for (const line of lines){
        const result = parseLine(line);
        cleanLines.push(result.cleanedLine);

        if(result.isAnnotationLine){
            if(result.annotation.object == "self"){
                // Simple function annotation
                functionAnnotations.push({
                    "property": result.annotation.property,
                    "value": result.annotation.value,
                });
            }else {
                // Complete triple
                triples.push(result.annotation);
            }
        }

    }

    cleanedCode = cleanLines.join("\n");

    return {
        "functionCode": cleanedCode,
        "functionAnnotations": functionAnnotations,
        "triples": triples,
    };
}
return msg;

